function BrainSocketPubSub(){this.subscriptions=[];this.forget=function(a){for(x=0;x<this.subscriptions.length;x++){if(this.subscriptions[x].name==a[0],this.subscriptions[x].callback==a[1]){this.subscriptions.splice(x,1)}}};this.listen=function(a,b){this.subscriptions.push({name:a,callback:b});return[a,b]};this.fire=function(d,c){var b=[];if(this.subscriptions.length>0){for(var a=0;a<this.subscriptions.length;a++){if(this.subscriptions[a].name==d){b.push({fn:this.subscriptions[a].callback})}}for(a=0;a<b.length;a++){b[a].fn.apply(this,[c])}}}};